import React from 'react';
import './Tabs.css';

const tabTitles = ['About Me', 'Experiences', 'Recommended'];

function Tabs({ active, setActive }) {
  return (
    <div className="tabs-container">
      {tabTitles.map((title) => (
        <button
          key={title}
          className={`tab-button ${active === title ? 'active' : ''}`}
          onClick={() => setActive(title)}
        >
          {title}
          {/* This is the blue line for the active tab */}
          {active === title && <div className="active-indicator"></div>}
        </button>
      ))}
    </div>
  );
}

export default Tabs;
