import React, { useState } from 'react';
import './GalleryWidget.css';

const AddIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="#6B7280" strokeWidth="2"/>
    <path d="M12 8V16M8 12H16" stroke="#6B7280" strokeWidth="2" strokeLinecap="round"/>
  </svg>
);

function GalleryWidget() {
  const [images, setImages] = useState([
    '/images/sampleimg.png',
    '/images/sampleimg.png',
    '/images/sampleimg.png',
  ]);

  const handleAddImage = () => {
    setImages([...images, '/images/sampleimg.png']);
  };

  return (
    <div className="gallery-card">
      <div className="gallery-header">
        <h3 className="gallery-title">Gallery</h3>
        <button className="add-image-btn" onClick={handleAddImage}>
          <AddIcon />
          <span>ADD IMAGE</span>
        </button>
      </div>
      
      <div className="gallery-container">
        {images.length > 0 ? (
          images.map((img, idx) => (
            <img key={idx} src={img} alt="gallery" className="gallery-img" />
          ))
        ) : (
          <div className="gallery-placeholder">
            <div className="placeholder-text">No images added yet</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default GalleryWidget;
