import React, { useState } from 'react';
import Tabs from './Tabs';
import './AboutWidget.css';

const QuestionIcon = () => (
  <svg
    width="20"
    height="20"
    viewBox="0 0 24 24"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
  >
    {/* Circle */}
    <circle cx="12" cy="12" r="10" stroke="#A3B1CC" strokeWidth="2" fill="none" />
    
    {/* Question mark path */}
    <path
      d="M9.5 9a2.5 2.5 0 1 1 5 0c0 1.5-2 2-2 4"
      stroke="#A3B1CC"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
      fill="none"
    />
    {/* Dot */}
    <circle cx="12" cy="17" r="1" fill="#A3B1CC" />
  </svg>
);

const SquaresIcon = () => (
  <svg width="20" height="20" fill="#A3B1CC" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
    <rect x="3" y="3" width="5" height="5" rx="1" />
    <rect x="10" y="3" width="5" height="5" rx="1" />
    <rect x="17" y="3" width="5" height="5" rx="1" />
    <rect x="3" y="10" width="5" height="5" rx="1" />
    <rect x="10" y="10" width="5" height="5" rx="1" />
    <rect x="17" y="10" width="5" height="5" rx="1" />
  </svg>
);

function AboutWidget() {
  const [activeTab, setActiveTab] = useState('About Me');

  return (
    <div className="about-card">
      <Tabs active={activeTab} setActive={setActiveTab} icons={{ QuestionIcon, SquaresIcon }} />

      <div className="about-content">
        {activeTab === 'About Me' && (
          <p>
            Hello! I'm Dave, your sales rep here from Salesforce. I've been working at this awesome company for 3 years now.<br/><br/>
