import React, { useState } from 'react';
import Tabs from './Tabs';
import './AboutWidget.css';

// Simple SVG icons for question mark and squares:
const QuestionIcon = () => (
  <svg width="20" height="20" fill="#A3B1CC" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 17a1.5 1.5 0 110-3 1.5 1.5 0 010 3zm1.07-7.75l-.9.92C11.45 12.9 11 13.5 11 15h2v-.5c0-1 .45-1.5 1.07-2.25.59-.7.93-1.38.93-2.25 0-1.54-1.28-2.5-3-2.5-1.49 0-2.5 1.02-2.5 1.02l1.15 1.14s.53-.68 1.35-.68c.7 0 1.2.44 1.2 1.12 0 .61-.3.95-.63 1.28z"/>
  </svg>
);

const SquaresIcon = () => (
  <svg width="18" height="18" fill="#A3B1CC" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
    <rect x="2" y="2" width="6" height="6" rx="1" />
    <rect x="9" y="2" width="6" height="6" rx="1" />
    <rect x="16" y="2" width="6" height="6" rx="1" />
    <rect x="2" y="9" width="6" height="6" rx="1" />
    <rect x="9" y="9" width="6" height="6" rx="1" />
    <rect x="16" y="9" width="6" height="6" rx="1" />
  </svg>
);

function AboutWidget() {
  const [activeTab, setActiveTab] = useState('About Me');

  return (
    <div className="about-card">
      <div className="about-header">
        <div className="icon-container">
          <QuestionIcon />
          <SquaresIcon />
        </div>
        <Tabs active={activeTab} setActive={setActiveTab} />
      </div>

      <div className="about-content">
        {activeTab === 'About Me' && (
          <p>
            Hello! I'm Dave, your sales rep here from Salesforce. I've been working at this awesome company for 3 years now.<br/><br/>
            I was born and raised in Albany, NY & have been living in Santa Carla for the past 10 years my wife Tiffany and my 4 year old twin daughters- Emma and Ella. Both of them are just starting school, so my calendar is usually blocked between 9-10 AM. This is a...
          </p>
        )}

        {activeTab === 'Experiences' && (
          <p>
            Iâ€™ve spent over a decade helping clients grow their businesses through Salesforce solutions, focusing on CRM and customer engagement.
          </p>
        )}

        {activeTab === 'Recommended' && (
          <p>
            Dave is highly recommended for his expertise, communication, and dedication to his clients' success.
          </p>
        )}
      </div>
    </div>
  );
}

export default AboutWidget;
