import React, { useState } from 'react';
import './GalleryWidget.css';

// ... (keep all the icons same as before)

function GalleryWidget() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [images] = useState([
    '/images/sampleimg.png',
    '/images/sampleimg.png', 
    '/images/sampleimg.png',
    '/images/sampleimg.png',
    '/images/sampleimg.png'
  ]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex + 3 >= images.length ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? Math.max(0, images.length - 3) : prevIndex - 1
    );
  };

  const visibleImages = images.slice(currentIndex, currentIndex + 3);

  return (
    <div className="gallery-card">
      <div className="gallery-header">
        <h3 className="gallery-title">Gallery</h3>
        <div className="gallery-controls">
          <button className="add-image-btn">
            + ADD IMAGE
          </button>
          <div className="nav-buttons">
            <button className="nav-btn" onClick={prevSlide}>
              <LeftArrow />
            </button>
            <button className="nav-btn" onClick={nextSlide}>
              <RightArrow />
            </button>
          </div>
        </div>
      </div>
      
      <div className="gallery-body">
        <div className="question-icon-top">
          <QuestionIcon />
        </div>
        
        <div className="icons-column">
          <SquaresIcon className="squares-icon" />
        </div>

        <div className="gallery-container">
          {visibleImages.map((img, idx) => (
            <div key={idx} className="image-wrapper">
              <img src={img} alt="gallery" className="gallery-img" />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default GalleryWidget;
