import React from 'react';
import './Tabs.css';

const tabTitles = ['About Me', 'Experiences', 'Recommended'];

function Tabs({ active, setActive, icons }) {
  const { QuestionIcon, SquaresIcon } = icons || {};
  const tabTitles = ['About Me', 'Experiences', 'Recommended'];
  const activeIndex = tabTitles.indexOf(active);

  return (
    <div className="tabs-container" style={{ '--active-index': activeIndex }}>
      <div className="icons-wrapper">
        {QuestionIcon && <QuestionIcon />}
        {SquaresIcon && <SquaresIcon />}
      </div>

      {tabTitles.map((title) => (
        <button
          key={title}
          className={`tab-button ${active === title ? 'active' : ''}`}
          onClick={() => setActive(title)}
        >
          {title}
        </button>
      ))}

      <div className="active-slider" />
    </div>
  );
}


export default Tabs;
